PUT elasticsearch-analyzer-index
{
  "settings": {
    "analysis": {
      "analyzer": {
        "my_custom_analyzer": {
          "type": "custom",
          "char_filter": [
            "emoticons" ,
            "html_strip"
          ],
          "tokenizer": "punctuation", 
          "filter": [
            "lowercase",
            "english_stop",
            "asciifolding"
          ]
        }
      },
      "tokenizer": {
        "punctuation": { 
          "type": "pattern",
          "pattern": "[ .,!?。]"
        }
      },
      "char_filter": {
        "emoticons": { 
          "type": "mapping",
          "mappings": [
            ":) => _happy_",
            ":( => _sad_",
            "🐂 => 牛"
